eval("App.ApplicationRoute = Ember.Route.extend({\n  renderTemplate: function() {\n    this._super();\n    \n    // Don't block this render\n    Ember.run.next(this, function() {\n      this.render(\"sidebar\", {\n        outlet: \"sidebar\",\n        into: \"application\",\n        controller: this.controllerFor(\"sidebar\")\n      });\n    });\n  },\n  \n  actions: {    \n    markAllRead: function(models) {\n      var cachedModels = {};\n      var ids = [];\n      var cachedStatuses = {};\n      \n      models.forEach(function(model) {\n        var id = model.get(\"id\");\n        if (!model.get(\"read\")) {\n          ids.push(id);\n        }\n        cachedStatuses[id] = model.get(\"read\");\n        cachedModels[id] = model;\n        model.set(\"read\", true);\n      });\n      \n      if (ids.length <= 0) {\n        return; \n      }\n      \n      App.API.markFeedItemsRead(ids)\n      .then(function(results) {\n        results.forEach(function(result) {\n          var cachedModel = cachedModels[result.feed_item_id];\n          if (!cachedModel) {\n            return;\n          }\n          cachedModel.setProperties(result);\n        });\n      }, function() {\n        models.forEach(function(model) {\n          model.set(\"read\", cachedStatuses[model.get(\"id\")]);\n        });\n      });\n    }\n  }\n});//@ sourceURL=client/app/routes/application_route.js")

